const center = document.querySelector('.center');
const firstShadow = center.attachShadow({mode: 'open'});

const firstShadow01 = document.createElement('div');
const firstShadow02 = document.createElement('div');

firstShadow01.appendChild(firstShadow02);

firstShadow01.classList.add('inner-a');
firstShadow02.classList.add('inner-b');

firstShadow.appendChild(firstShadow01);

const secondShadow = firstShadow02.attachShadow({mode: 'open'});
const secondShadow01 = document.createElement('div');
secondShadow01.classList.add('pickr-target');
secondShadow.appendChild(secondShadow01);


const pickr = new Pickr({
    el: '.center >> .inner-a .inner-b >> .pickr-target',

    default: '#42445A',
    showAlways: true,
    appClass: 'hello-world',

    swatches: [
        'rgba(244, 67, 54, 1)',
        'rgba(233, 30, 99, 0.95)',
        'rgba(156, 39, 176, 0.9)',
        'rgba(103, 58, 183, 0.85)',
        'rgba(63, 81, 181, 0.8)',
        'rgba(33, 150, 243, 0.75)',
        'rgba(3, 169, 244, 0.7)',
        'rgba(0, 188, 212, 0.7)',
        'rgba(0, 150, 136, 0.75)',
        'rgba(76, 175, 80, 0.8)',
        'rgba(139, 195, 74, 0.85)',
        'rgba(205, 220, 57, 0.9)',
        'rgba(255, 235, 59, 0.95)',
        'rgba(255, 193, 7, 1)'
    ],

    components: {

        preview: true,
        opacity: true,
        hue: true,

        interaction: {
            hex: true,
            rgba: true,
            hsva: true,
            input: true,
            cmyk: true,
            clear: true,
            save: true
        }
    }
});

pickr.on('init', (...args) => {
    console.log('init', args);
}).on('save', (...args) => {
    console.log('save', args);
}).on('change', (...args) => {
    console.log('change', args);
}).on('swatchselect', (...args) => {
    console.log('swatchselect', args);
});
